mapper.select:
SELECT diary_no, title, content, reg_date, user_id
FROM diary
WHERE user_id = #{userId}
ORDER BY reg_date DESC
LIMIT #{offset}, #{limit};

SELECT COUNT(*)
FROM diary
WHERE user_id = #{userId}

SELECT diary_no, title, content, reg_date, user_id
FROM diary
WHERE user_id = #{userId} AND reg_date = #{regDate}

SELECT diary_no, title, content, reg_date, user_id
FROM diary
WHERE diary_no = #{diaryNo}

mapper.update:
UPDATE diary 
SET title = #{title}, content = #{content}, regDate = NOW() 
WHERE diary_no = #{diaryNo}
